import React, {useCallback, useState} from "react";

 export default function useModal() {
    const [modalState, setModalState] = useState(false);

    const openModal = useCallback(() => {
        setModalState(true);
    }, []);

    const closeModal = useCallback(() => {
        setModalState(false);
    }, []);

    return {
        modalState,
        openModal,
        closeModal,
    };
}
