import React, {useState} from "react";
import Price from "../Price/Price";
import styles from "../Card/Card.module.css";
import clsx from "clsx";
import {Counter} from "@ya.praktikum/react-developer-burger-ui-components";
import PropTypes from 'prop-types';
import Modal from "../Modal/Modal";
import IngredientDetails from "../Ingredient-details/Ingredient-details";

export default function Card({card}) {

    const [isModalOpen, setIsModalOpen] = useState(false);

    const openModal = () => {
        setIsModalOpen(true);
    }

    const closeModal = () => {
        setIsModalOpen(false);
    }

    return (
        <>
        <li onClick={openModal} className={clsx(styles.card)}>
            <div className={styles.counter}>
                <Counter count={1} size="default"/>
            </div>
            <img className='mr-4 ml-4' src={card.image} alt={card.name}/>
            <Price price={card.price}></Price>
            <p className='text_type_main-default'>{card.name}</p>
        </li>
        {isModalOpen && <Modal closeModal={closeModal}><IngredientDetails card={card}/></Modal>}
        </>
    )
}

Card.propTypes = {
    name: PropTypes.string,
    image: PropTypes.string,
    price: PropTypes.number
};
