import {createAsyncThunk, createSlice} from "@reduxjs/toolkit";
import {getCard} from "../utils/Api";

const initialState = {
    cardsArray: [],
    isLoading: false,
    error: ''
};

const takeCards = createAsyncThunk(
    'getCards',
    async () => {
        const res = await getCard();
        return res;
    }
)

const cardsSlice = createSlice({
    name: 'cards',
    initialState,
    reducers: {
        cardsUploading: (state,action) => {
        state.isLoading = true;
        state.error = '';
        },
        cardsLoadError: (state,action) => {
        state.error = action.payload;
        state.isLoading = false;
        },
        cardsLoaded: (state, action) => {
            state.cardsArray = action.payload;
            state.isLoading = false;
        }
    },
    extraReducers: {

    }
})

export default cardsSlice.reducer;
export const {cardsLoaded, cardsUploading, cardsLoadError} = cardsSlice.actions;
