import {createSlice} from "@reduxjs/toolkit";
import {store} from "../index";

const initialState = {
    constructorIngredient: []
};

const constructorSlice = createSlice({
    name: 'ingredient',
    initialState,
    reducers: {
        addIngredient: (state,action) => {
            console.log(store.reducer.burgerConstructor);
            // if (state.constructorIngredient.includes(card.type === "bun"))
            state.constructorIngredient.push(action.payload);
        },
        removeIngredient: (state,action) => {
            state.constructorIngredient = state.constructorIngredient.filter((ingredient) => ingredient._id !== action.payload);
        },
    }
})

export default constructorSlice.reducer;
export const {addIngredient, removeIngredient} = constructorSlice.actions;
