import {createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import {postOrderRequest} from "../utils/Api";

const initialState = {
    order: null,
    loading: true,
    error: null
};

export const postOrderResult = createAsyncThunk(
    `${sliceName}/postOrderResult`,
    async (ingredients) => {
        return await postOrderRequest(ingredients).then((data) => data.data);
    }
);

export const orderDetailsSlice = createSlice({
    name: 'orderDetails',
    initialState,
    extraReducers: builder => {
        builder
            .addCase(postOrderResult.pending, state => {
                state.loading = true;
                state.error = null;
            })

            .addCase(postOrderResult.fulfilled, (state, action) => {
                state.loading = false;
                state.order = action.payload;
                state.error = null;
            })

            .addCase(postOrderResult.rejected, (state, action) => {
                state.loading = false;
                state.error = action.error.message;
                state.order = null;
            });
    }
});

export default orderDetailsSlice.reducer;
