import clsx from "clsx";
import styles from "../Section-burger-ingredients/Section-burger-ingredients.module.css";
import Container from "../Container/Container";
import {Tab} from "@ya.praktikum/react-developer-burger-ui-components";
import Card from "../Card/Card";
import Modal from "../Modal/Modal";
import IngredientDetails from "../Ingredient-details/Ingredient-details";
import React, {useEffect, useState} from "react";
import useCards from "../../hooks/useCards";

export default function SectionBurgerIngredients (){
    useCards();

    const [isModalOpen, setIsModalOpen] = useState(true);

    const openModal = () => {
        setIsModalOpen(true);
    }

    const closeModal = () => {
        setIsModalOpen(false);
    }

    return (
        <section className={clsx(styles.burgerIngredients, 'mr-10')}>
            <h1 className='text text_type_main-large mt-10'>Соберите
                бургер</h1>
            <div className={clsx(styles.tabs, 'mt-5 mb-10')}>
                <Container>
                    <Tab>Булки</Tab>
                </Container>
                <Container>
                    <Tab>Соусы</Tab>
                </Container>
                <Container>
                    <Tab>Начинки</Tab>
                </Container>
            </div>
            <div className={styles.cardsContainer}>
                <h2 className='text text_type_main-medium'>Булки</h2>
                <ul className={clsx(styles.cards, 'mt-6 ml-4')}>
                    {cards.map((card) => (
                        card.type === "bun" && <Card openModal={openModal} {...card} key={card._id}/>
                    ))}
                </ul>

                <h2 className='text text_type_main-medium mt-10'>Соусы</h2>
                <ul className={clsx(styles.cards, 'mt-6 ml-4')}>
                    {cards.map((card) => (
                        card.type === "sauce" &&
                        <Card openModal={openModal} priceSize={"default"} {...card} key={card._id}/>
                    ))}
                </ul>
                <h2 className='text text_type_main-medium mt-10'>Начинки</h2>
                <ul className={clsx(styles.cards, 'mt-6 ml-4')}>
                    {cards.map((card) => (
                        card.type === "main" &&
                        <Card openModal={openModal} priceSize={"default"} {...card} key={card._id}/>
                    ))}
                </ul>
            </div>
            {isModalOpen && <Modal closeModal={closeModal}><IngredientDetails/></Modal>}
        </section>
    )
}
