import {useCallback, useState} from "react";

export default function useModal() {
    const [isModalState, setModalState] = useState(false);

    const openModal = useCallback(() => {
        setModalState(true);
    }, []);

    const closeModal = useCallback(() => {
        setModalState(false);
    }, []);

    return {
        isModalState,
        openModal,
        closeModal,
    };
}
