import {createSlice} from "@reduxjs/toolkit";


const initialState = {
    cardsArray: []
};

const cardsSlice = createSlice({
    name: 'cards',
    initialState,
    reducers: {
        addCard: (state,action) => {
        state.cardsArray = action.payload
        }
    }
})

export default cardsSlice.reducer;
export const {addCard} = cardsSlice.actions;
