import {createSlice} from "@reduxjs/toolkit";

const initialState = {
    isModalState: false,
    title: '',
    content: null
};

const popupInfoSlice = createSlice({
    name: 'popupInfo',
    initialState,
    reducers: {
        openPopup: (state,action) => {
            state.isModalState = true;
            action.title = action.payload.title;
            state.content = action.payload.content;
        },
        closePopup: (state,action) => {
            state.isModalState = false;
            state.title = '';
            state.content = null;
        },
    }
})

export default popupInfoSlice.reducer;
export const {openPopup, closePopup} = popupInfoSlice.actions;
