import {createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import {postOrderRequest} from "../utils/Api";

const initialState = {
    order: null,
    loading: true,
    error: null
};

export const postOrderResult = createAsyncThunk(
    `orderDetails`,
    async (ingredients, { rejectWithValue, fulfillWithValue }) => {
        try {
            const response = await postOrderRequest(ingredients);

            if (!response.ok) {
                return rejectWithValue({ message: `Ошибка при загрузке данных: ${response.status}` });
            }

            const data = await response.json();
            return fulfillWithValue(data);
        } catch (error) {
            return rejectWithValue({ message: `Ошибка при загрузке данных: ${error.message}` });
        }
    }
);

export const orderDetailsSlice = createSlice({
    name: 'orderDetails',
    initialState,
    reducers: {},
    extraReducers: builder => {
        builder
            .addCase(postOrderResult.pending, state => {
                state.loading = true;
                state.error = null;
            })

            .addCase(postOrderResult.fulfilled, (state, action) => {
                state.loading = false;
                state.order = action.payload.order;
                state.error = null;
            })

            .addCase(postOrderResult.rejected, (state, action) => {
                state.loading = false;
                state.error = action.error.message;
                state.order = null;
            });
    }
});

export default orderDetailsSlice.reducer;
