import {useDispatch} from "react-redux";

const dispatch = useDispatch();

export const actionCards = () => {
    dispatch({
        type: 'ADD',
        payload: 1234,
    })
};


export const ADDaction = (payload) => {
    return {
        type: ADD,
        payload: payload,
    }
};
